document.addEventListener("DOMContentLoaded", function () {
  var navItems = document.querySelectorAll(
    ".navbar-nav .nav-item:not(:last-child)"
  );
  var logoutButton = document.querySelector(".navbar-nav .nav-item:last-child");

  function removeActiveClasses() {
    navItems.forEach(function (item) {
      item.classList.remove("active");
    });
  }

  navItems.forEach(function (item) {
    item.addEventListener("click", function () {
      if (!this.classList.contains("active")) {
        removeActiveClasses();
        this.classList.add("active");
      }
    });
  });

  logoutButton.addEventListener("click", function () {
    removeActiveClasses();
  });

  var reportGenerationItem = document.querySelector(
    ".navbar-nav .nav-item:nth-child(4)"
  );

  reportGenerationItem.addEventListener("click", function () {
    if (!this.classList.contains("active")) {
      removeActiveClasses();
      this.classList.add("active");
    }
  });

  navItems.forEach(function (item) {
    item.addEventListener("click", function () {
      if (
        reportGenerationItem.classList.contains("active") &&
        this !== reportGenerationItem
      ) {
        reportGenerationItem.classList.remove("active");
      }
    });
  });
});

document.addEventListener("DOMContentLoaded", function () {
  var navItems = document.querySelectorAll(
    ".navbar-nav .nav-item:not(:last-child)"
  );
  var contentDiv = document.getElementById("content");

  function removeActiveClasses() {
    navItems.forEach(function (item) {
      item.classList.remove("active");
    });
  }

  navItems.forEach(function (item) {
    item.addEventListener("click", function () {
      removeActiveClasses();
      this.classList.add("active");

      var itemText = this.textContent.trim();
      contentDiv.innerHTML = `<div class="text-center">${itemText}</div>`;
    });
  });
});
